
var BaseNode = require('./BaseNode');

var __floatPrecision = 4;


var __colorNormal = new THREE.Color(0.6, 0.6, 0.4);
var __colorHighlight = new THREE.Color(0.8, 0.8, 0.4);

function NumberNode(value, floatPrecision) {
	this.colorNormal = this.colorNormal || __colorNormal;
	this.colorHighlight = this.colorHighlight || __colorHighlight;
	this.floatPrecision = floatPrecision || __floatPrecision;
	if(value === undefined) {
		value = ~~(Math.random() * 100 + 50);
	}
	BaseNode.call(this);
	this.addInput();
	this.addOutput();
	this.cachedArguments[0] = value;
	this.updateOutput();
}

NumberNode.prototype = Object.create(BaseNode.prototype);

NumberNode.prototype.updateOutput = function() {
	BaseNode.prototype.updateOutput.call(this);
	this.label.updateText(this.outputValue.toFixed(this.floatPrecision));
};

module.exports = NumberNode;
