
var NumberNode = require('./NumberNode');


var __colorNormal = new THREE.Color(0.6, 0.4, 0.4);
var __colorHighlight = new THREE.Color(0.8, 0.6, 0.4);

function TimeNode() {
	this.colorNormal = this.colorNormal || __colorNormal;
	this.colorHighlight = this.colorHighlight || __colorHighlight;

	NumberNode.call(this);
	this.removeInput();
	this.cachedArguments[0] = Date.now();
	this.updateOutput();
}

TimeNode.prototype = Object.create(NumberNode.prototype);

TimeNode.prototype.onEnterFrame = function() {
	var now = Date.now();
	this.updateInput(0, now);
	this.label.updateText(String(now));
};

module.exports = TimeNode;
